import React, { useState, useEffect } from "react";
import { Card, CardContent, Typography, Grid } from "@material-ui/core";
import { fetchData } from "../api/fetchApi";
import cx from "classnames";
import styles from "./Summary.module.css";
import CountUp from "react-countup";

const Summary = () => {
  const [fetchedState, setFetchedState] = useState([]);

  useEffect(() => {
    const fetchAPIState = async () => {
      const dailyData = await fetchData();
      setFetchedState(dailyData);
      //console.log(dailyData)
    };
    fetchAPIState();
  }, []);
  const arunachalTotal = parseInt(fetchedState.lohit);
  const arunachalRecovered = parseInt(fetchedState.lohitrecovered);
  const assamTotal =
    parseInt(fetchedState.bongaigaon) +
    parseInt(fetchedState.cachar) +
    parseInt(fetchedState.dhubri) +
    parseInt(fetchedState.goalpara) +
    parseInt(fetchedState.golaghat) +
    parseInt(fetchedState.hailakandi) +
    parseInt(fetchedState.kamrup) +
    parseInt(fetchedState.kamrupmetro) +
    parseInt(fetchedState.lakhimpur) +
    parseInt(fetchedState.morigaon) +
    parseInt(fetchedState.nalbari) +
    parseInt(fetchedState.salmara) +
    parseInt(fetchedState.unknownassam);
  const assamRecovered =
    parseInt(fetchedState.bongaigaon) +
    parseInt(fetchedState.cachar) +
    parseInt(fetchedState.dhubri) +
    parseInt(fetchedState.goalpara) +
    parseInt(fetchedState.golaghat) +
    parseInt(fetchedState.hailakandi) +
    parseInt(fetchedState.kamrup) +
    parseInt(fetchedState.kamrupmetro) +
    parseInt(fetchedState.lakhimpur) +
    parseInt(fetchedState.morigaon) +
    parseInt(fetchedState.nalbari) +
    parseInt(fetchedState.salmara) +
    parseInt(fetchedState.unknownassam);
  const manipurTotal =
    parseInt(fetchedState.imphalwest) + parseInt(fetchedState.imphaleast);
  const meghalayaTotal = parseInt(fetchedState.eastkhasi);
  const mizoramTotal = parseInt(fetchedState.aizawl);
  // const nagalandTotal = parseInt(fetchedState.dimapur)
  const tripuraTotal =
    parseInt(fetchedState.gomati) + parseInt(fetchedState.northtripura);

  const total =
    arunachalTotal +
    assamTotal +
    manipurTotal +
    meghalayaTotal +
    mizoramTotal +
    tripuraTotal;
  //   if (!total) {
  //     return "Loading...";
  //     //   console.log(total);
  //   }
  return (
    <div className={styles.container}>
      <Grid container spacing={3} justify="center">
        <Grid
          item
          component={Card}
          xs={12}
          md={3}
          className={cx(styles.card, styles.infected)}
        >
          {/* Card content detailing all the data from the url */}
          <CardContent>
            <Typography
              color="textSecondary"
              style={{ fontSize: "18px", textAlign: "center" }}
              gutterBottom
            >
              Total Confirmed in NE-India
            </Typography>
            <Typography variant="h5" style={{ textAlign: "center" }}>
              {total || "Loading.."}
            </Typography>
          </CardContent>
        </Grid>
        <Grid
          item
          component={Card}
          xs={12}
          md={3}
          className={cx(styles.card, styles.recovered)}
        >
          {/* Card content detailing all the data from the url */}
          <CardContent>
            <Typography
              color="textSecondary"
              style={{ fontSize: "18px", textAlign: "center" }}
              gutterBottom
            >
              Total Recovered in India
            </Typography>
            <Typography variant="h5" style={{ textAlign: "center" }}>
              <CountUp start={0} end={total} duration={2.5} separator="," />
            </Typography>
          </CardContent>
        </Grid>

        <Grid
          item
          component={Card}
          xs={12}
          md={3}
          className={cx(styles.card, styles.deaths)}
        >
          {/* Card content detailing all the data from the url */}
          <CardContent>
            <Typography
              color="textSecondary"
              style={{ fontSize: "18px", textAlign: "center" }}
              gutterBottom
            >
              Total Deaths in India
            </Typography>
            <Typography variant="h5" style={{ textAlign: "center" }}>
              <CountUp start={0} end={total} duration={2.5} separator="," />
            </Typography>
            {/* <Typography variant="body2">deaths</Typography> */}
          </CardContent>
        </Grid>
      </Grid>
    </div>
  );
};

export default Summary;
